<script type="text/javascript" >
function format_currency(amount) {
        var i = parseFloat(amount);
        if(isNaN(i)) { i = 0.00; }
        var minus = '';
        if(i < 0) { minus = '-'; }
        i = Math.abs(i);
        i = parseInt((i + .005) * 100);
        i = i / 100;
        s = new String(i);
        if(s.indexOf('.') < 0) { s += '.00'; }
        if(s.indexOf('.') == (s.length - 2)) { s += '0'; }
        s = minus + s;
        return s;
}

function calculate_finance() {
  
  //spread fees option
  var spread_finance_fees = parseInt ($("#spread_finance_fees").val());
  
  var credit_rating = document.getElementById('finance_credit_rating');

  var fixed_rate = document.getElementById('finance_rate_fixed');
  var this_rate = document.getElementById('finance_rate_' + credit_rating.value);
  fixed_rate.value = this_rate.value;

  var acceptance_fee = document.getElementById('finance_acceptance_fee');
  var this_acceptance_fee = document.getElementById('finance_acceptance_fee_' + credit_rating.value);
  acceptance_fee.value = this_acceptance_fee.value;

  var option_fee = document.getElementById('finance_option_fee');
  var this_option_fee = document.getElementById('finance_option_fee_' + credit_rating.value);
  option_fee.value = this_option_fee.value;


  var princ = document.getElementById('finance_principal');
  
  //check for minimum deposit
 	//check new deposit isn't less than min
  $("#deposit_error_message").html('');
  if (Number($("#finance_deposit_minimum").val()) > 0) {
		if (Number($("#finance_deposit").val()) < Number($("#finance_deposit_minimum").val())) {
			//reset deposit
			$("#finance_deposit").val($("#finance_deposit_minimum").val());
			$("#deposit_error_message").html('<em><img src="/img-src/themev2/warning-icon-red.png" align="absmiddle" height="15px"/> Deposit can\'t be less than &pound;' + $("#finance_deposit_minimum").val() + '</em>');
		}
  }
  
   var deposit = document.getElementById('finance_deposit');
 
  
  var term = document.getElementById('finance_term');
  var payment = document.getElementById('finance_payment');
  var fiddle_factor = document.getElementById('finance_fiddle_factor');
  var first_payment = document.getElementById('finance_first_payment');
  var final_payment = document.getElementById('finance_final_payment');
  var balance = document.getElementById('finance_balance');
  var total = document.getElementById('finance_total');
  var total_inc_deposit = document.getElementById('finance_total_inc_deposit');

  var num_payments = document.getElementById('finance_num_payments');
  num_payments.textContent = term.value - 2;

  princ.value = format_currency(balance.value - deposit.value);

  var intr = (parseFloat(term.value)/12) * parseFloat(princ.value) * parseFloat(fixed_rate.value) / 100;
  total.value = format_currency(parseFloat(princ.value) + parseFloat(acceptance_fee.value) + parseFloat(option_fee.value) + intr);
  total_inc_deposit.value = format_currency(parseFloat(total.value) + parseFloat(deposit.value));
  var rate1 = 100 * (parseFloat(total.value) - parseFloat(princ.value)) / parseFloat(princ.value);
  var actual_rate = 10 * rate1 / parseFloat(term.value);

  var rate = document.getElementById('finance_rate');
  rate.value = format_currency((actual_rate * 2) + parseFloat(fiddle_factor.value));

  payment.value = format_currency((parseFloat(princ.value) + intr) / parseFloat(term.value));
	
	if (spread_finance_fees==1) {
		payment.value = format_currency((parseFloat(princ.value) + parseFloat(acceptance_fee.value) + parseFloat(option_fee.value) + intr) / parseFloat(term.value));
	}
	


/* -- Accounts for rounding error but inflates final payment
  final_payment.value = format_currency(parseFloat(total.value) - (num_payments.value * payment.value));
*/

/* May have rounding errors but final payment is exactly monthly fee + option fee */
  first_payment.value = format_currency(parseFloat(payment.value) + parseFloat(acceptance_fee.value));
  final_payment.value = format_currency(parseFloat(payment.value) + parseFloat(option_fee.value));

	if (spread_finance_fees==1) {
		first_payment.value = format_currency(parseFloat(payment.value));
  		final_payment.value = format_currency(parseFloat(payment.value));	
	}

	var new_apr = rate.value;
	new_apr = parseFloat(new_apr).toFixed(1);
	rate.value = new_apr;

	//update table values

	try {
		document.getElementById('td_finance_term').innerHTML = term.value + ' mths';
		document.getElementById('td_finance_deposit').innerHTML = '&pound;' + format_currency(deposit.value);
		document.getElementById('td_finance_balance').innerHTML = '&pound;' + format_currency(princ.value);
		document.getElementById('td_finance_total_inc_deposit').innerHTML = '&pound;' + total_inc_deposit.value;
		document.getElementById('td_finance_rate').innerHTML = this_rate.value + '%';
		document.getElementById('td_finance_apr').innerHTML = new_apr + '%';
		document.getElementById('td_finance_first_payment').innerHTML = '&pound;' + first_payment.value;
		document.getElementById('td_finance_final_payment').innerHTML = '&pound;' + final_payment.value;
		document.getElementById('td_finance_monthly_payments').innerHTML = '&pound;' + payment.value + ' x ' + num_payments.textContent;
		
		document.getElementById('tdmb_finance_term').innerHTML = term.value + ' mths';
		document.getElementById('tdmb_finance_deposit').innerHTML = '&pound;' + format_currency(deposit.value);
		document.getElementById('tdmb_finance_balance').innerHTML = '&pound;' + format_currency(balance.value);
		document.getElementById('tdmb_finance_total_inc_deposit').innerHTML = '&pound;' + total_inc_deposit.value;
		document.getElementById('tdmb_finance_rate').innerHTML = this_rate.value + '%';
		document.getElementById('tdmb_finance_apr').innerHTML = new_apr + '%';
		document.getElementById('tdmb_finance_first_payment').innerHTML = '&pound;' + first_payment.value;
		document.getElementById('tdmb_finance_final_payment').innerHTML = '&pound;' + final_payment.value;
		document.getElementById('tdmb_finance_monthly_payments').innerHTML = '&pound;' + payment.value + ' x ' + num_payments.textContent;
		
	} catch(e) { }

}
</script>

